import React from 'react';
import './App.css';

class App extends React.Component {
  constructor() {
    super()

    this.handleClick = this.handleClick.bind(this)
    this.Click = this.Click.bind(this)
    this.handle = this.handle.bind(this)

    this.state = {
      numeroCliques: 0,
      numero: 0,
      num : 0
    }

  }

  handleClick() {
    this.setState(({numero}) => ({
      numero:numero + 1
    }))
  }
  Click() {
    this.setState(({num}) => ({
      num: num + 1
    }))
  }
  handle() {
    this.setState(({numeroCliques}) => ({
      numeroCliques: numeroCliques + 1
    }))
  }

  getButtonColor(num) {
    // Essa função contém um ternário que realiza a lógica para mudarmos
    // a cor do botão para verde quando for um número par
    return num % 2 === 0 ? 'green' : 'white';
  }


  render() {
    const {handle, handleClick, Click} = this.state;

    return (
      <div>
        <button onClick={this.handleClick}
        style={{backgroundColor: this.getButtonColor(handleClick)}}> Botão 1| Count = {numero}</button>
        <button onClick={this.Click}
        style={{backgroundColor:this.getButtonColor(Click)}}> Botão 2 | Count = {num}</button>
        <button onClick={this.handle} style={{backgroundColor: this.getButtonColor(handle)}}>Botão 3 | Count = {numeroCliques}</button>

      </div>
    )
  }
}

export default App;
