import React from 'react';
import './App.css';

class App extends React.Component {
  constructor() {
    super()

    this.handleClick = this.handleClick.bind(this)
    this.Click = this.Click.bind(this)
    this.handle = this.handle.bind(this)

    this.state = {
      numeroCliques: 0,
      numero: 0,
      num : 0
    }

  }

  handleClick() {
    this.setState((antigo, _props) => ({
      numero: antigo.numero + 1
    }))
  }
  Click() {
    this.setState((antigo, _props) => ({
      num: antigo.num + 1
    }))
  }
  handle() {
    this.setState(({numeroCliques}) => ({
      numeroCliques: numeroCliques + 1
    }))
  }

  getButtonColor(num) {
    // Essa função contém um ternário que realiza a lógica para mudarmos
    // a cor do botão para verde quando for um número par
    return num % 2 === 0 ? 'green' : 'white';
  }


  render() {
    return (
      <div>
        <button onClick={this.handleClick}> Botão 1| Count = {this.state.numero}</button>
        <button onClick={this.Click}> Botão 2 | Count = {this.state.num}</button>
        <button onClick={this.handle}>Botão 3 | Count = {this.state.numeroCliques}</button>

      </div>
    )
  }
}

export default App;
