  constructor() {
    super()

    this.handleClick = this.handleClick.bind(this)
    this.Click = this.Click.bind(this)
    this.handle = this.handleClick.bind(this)

    this.state = {
      numeroCliques: 0
    }

  }

  handleClick() {
    console.log('Samuel!')
  }
  Click() {
    console.log('Jhuly!')
  }
  handle() {
    this.setState(( antigo, _props) => ({
      numeroCliques : antigo.numeroCliques +1
    }))
  }


  render() {
    return (
      <div> 
      <button onClick={this.handleClick}>Meu botão</button>
      <button onClick={this.Click}>Meu botão</button>
      <button onClick={this.handle}>{this.state.numeroCliques}</button>

      </div>
      )
  }
}

export default App;
